mapper.insert:
insert into ai_conversation_sessions (conversationId, userId

    , title


    , createdAt


    , updatedAt

) values (#{conversationId}, #{userId}

    , #{title}


    , #{createdAt}


    , #{updatedAt}

)

mapper.select:
select conversationId,title from ai_conversation_sessions
where userId = #{userId}
order by id desc
limit #{offset}, #{pageSize}
