mapper.insert:
insert into videos (
id,userId,userName,title,url, tags, category, description, duration
)values (
#{id},#{userId},#{userName},#{title},#{url}, #{tags, typeHandler=org.com.code.im.utils.StringListToJsonConverter}, #{category}, #{description}, #{duration}
)

mapper.select:
select id,userId,userName,title,views,likeCount,commentCount,tags,
       category,duration,createdAt
from videos


        id in

            #{id}

        and status = 'approved'
        order by field(id,

            #{id}

        )


        1 = 0
